function buscarCEP(){var e=$("#cep").val().replace("-","");$.ajax({type:"GET",url:"https://viacep.com.br/ws/"+e+"/json/",success:function(e){if(e.erro)swal({title:"CEP não encontrado!",text:"Você ainda pode preencher as informações de endereço manualmente.",showConfirmButton:!0});else{$("#endereco_numero").val(""),$("#endereco").val(e.logradouro),$("#complemento").val(e.complemento),$("#cidade").val(e.localidade),$("#bairro").val(e.bairro);var o=buscarIdEstado(e.uf);$(".estado option").removeAttr("selected").filter("[value="+o+"]").attr("selected",!0)}},error:function(e,o,a){swal({title:"CEP não encontrado!",text:"Não foi possível realizar a busca de CEP.",showConfirmButton:!0})}})}